from turtle import *"""http://algorithmicbotany.org/papers/abop/abop-ch1.pdf"""length = 10angle = 90def draw_path(path):    for symbol in path:        if symbol == "F":            forward(length)        elif symbol == '-':            left(angle)        elif symbol == '+':            right(angle)def apply_rule(path):    rule = "F-F+F+FF-F-F+F"    return path.replace("F", rule)path = "F-F-F-F"speed(0)path = apply_rule(path)path = apply_rule(path)path = apply_rule(path)draw_path(path)exitonclick()