from turtle import *import turtlelength = 10angle = 90rules = {    "Fl": "Fl+Fr+",    "Fr": "-Fl-Fr"}def split_path(path):    i = 0    lst = []    while i < len(path):        if path[i] == "F":            lst.append(path[i:i + 2])            i = i + 2        else:            lst.append(path[i])            i = i + 1    return lstdef apply_rule(path, rules):    lst = split_path(path)    for i in lst:        if i in rules:            lst[lst.index(i)] = rules[i]    path = "".join(symbol for symbol in lst)    return pathdef draw_path(path):    lst = split_path(path)    for symbol in lst:        if symbol == "Fl" or symbol == "Fr":            forward(length)        elif symbol == "-":            left(angle)        elif symbol == "+":            right(angle)path = 'Fl+Fr+'speed(0)for i in range(10):    path = apply_rule(path, rules)draw_path(path)exitonclick()