"""4 1 1 9 1选出一堆数字,不能是相邻两个,选中的数字加起来最大arr [1 2 4 1  7 8 3]OPT(6) = sum(1 4 7 3)"""import numpy as npdef rec_opt(arr, i):    if i == 0:        return arr[0]    if i == 1:        return max(arr[0], arr[1])    else:        A = rec_opt(arr, i - 2) + arr[i]        B = rec_opt(arr, i - 1)        return max(A, B)def do_opt(arr):    opt = np.zeros(len(arr))    opt[0] = arr[0]    opt[1] = max(arr[0], arr[1])    for i in range(2, len(arr)):        A = opt[i - 2] + arr[i]        B = opt[i - 1]        opt[i] = max(A, B)    return opt[len(arr) - 1]if __name__ == '__main__':    # arr = [1, 2, 4, 1, 7, 8, 3]    arr = [4, 1, 1, 9, 1]    # print(rec_opt(arr, 6)) O(2^n)    print(do_opt(arr))