import datetimeimport mathdef cal_grain(grid):    sum = 0    for i in range(grid + 1):        sum += 2 << i    return sumdef recursion_cal_grain(grid):    if grid == 0:        return 1    # << 的优先级比　+ 低    return (2 << grid) + recursion_cal_grain(grid - 1)if __name__ == '__main__':    grid = 64    start = datetime.datetime.now()    print("舍罕王给了这么多粒: %d" % cal_grain(grid))    end = datetime.datetime.now()    print('使用循环　spend time: %s' % (end - start))    start = datetime.datetime.now()    print("舍罕王给了这么多粒: %d" % recursion_cal_grain(grid))    end = datetime.datetime.now()    print('使用递归　spend time: %s' % (end - start))    start = datetime.datetime.now()    print("舍罕王给了这么多粒: %d" % int(2 ** (grid + 1) - 1))    end = datetime.datetime.now()    print('使用公式　spend time: %s' % (end - start))