class Solution:    def compress(self, chars):        """        :type chars: List[str]        :rtype: int        """        key = chars[0]        count = 1        res = ''        for x, i in enumerate(chars[1:]):            if key == i:                count += 1                if x == len(chars[1:]) - 1:                    res += key + str(count)            else:                res += key + str(count)                key = i                count = 1        if len(chars) <= len(res):            res = chars        return list(res)if __name__ == '__main__':    chars = ["a","a","b","b","c","c","c", "a", "a"]    c = Solution()    print(c.compress(chars))